import React, { useEffect, useState } from "react";
import { Link } from "react-router-dom";

const StudentList = () => {
  const [students, setStudents] = useState([]);

  // 학생 목록 가져오기
  useEffect(() => {
    fetch("https://67281ba1270bd0b9755468a5.mockapi.io/ossexample")
      .then((res) => res.json())
      .then((data) => setStudents(data))
      .catch((err) => console.error("Failed to fetch students:", err));
  }, []);

  // 학생 삭제
  const handleDelete = async (id) => {
    const confirmDelete = window.confirm("Are you sure you want to delete this student?");
    if (!confirmDelete) return;

    try {
      const response = await fetch(`https://67281ba1270bd0b9755468a5.mockapi.io/ossexample/${id}`, {
        method: "DELETE",
      });

      if (response.ok) {
        alert("Student deleted successfully!");
        setStudents(students.filter((student) => student.id !== id)); // 목록 업데이트
      } else {
        throw new Error("Failed to delete student.");
      }
    } catch (error) {
      console.error("Error deleting student:", error);
    }
  };

  return (
    <div>
      <h1>Student List</h1>
      <table>
        <thead>
          <tr>
            <th>ID</th>
            <th>Name</th>
            <th>Age</th>
            <th>Gender</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {students.map((student) => (
            <tr key={student.id}>
              <td>{student.id}</td>
              <td>{student.name}</td>
              <td>{student.age}</td>
              <td>{student.gender}</td>
              <td>
                <Link to={`/update/${student.id}`}>
                  <button>Update</button>
                </Link>
                <button onClick={() => handleDelete(student.id)} style={{ marginLeft: "10px" }}>
                  Delete
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default StudentList;
